mapper.select:
select count(*) from t_ent_out_put_info i left join t_bas_enterprise e on e.ent_code = i.ent_code
and e.ent_name like concat('%', #{entName}, '%') and i.out_put_code like concat('%', #{outPutCode}, '%') and i.out_put_name like concat('%', #{outPutName}, '%') and i.out_put_type = #{outPutType} and i.out_put_status = #{outPutStatus} and i.mn_num like concat('%', #{mnNum}, '%') and e.ent_code in
#{code}

select i.out_put_id, e.ent_code, e.ent_name, i.out_put_code, i.out_put_name, i.out_put_type, i.out_put_status, i.longitude, i.latitude, i.out_put_height, i.out_put_position, i.mn_num, i.trans_rate, i.valid_rate, i.create_user, i.create_time, i.update_user, i.update_time, i.remark, i.pollutant_code from t_ent_out_put_info i left join t_bas_enterprise e on e.ent_code = i.ent_code
and i.out_put_id < #{outPutId} and e.ent_name like concat('%', #{entName}, '%') and i.out_put_code like concat('%', #{outPutCode}, '%') and i.out_put_name like concat('%', #{outPutName}, '%') and i.out_put_type = #{outPutType} and i.out_put_status = #{outPutStatus} and i.mn_num like concat('%', #{mnNum}, '%') and e.ent_code in
#{code}
order by i.out_put_id desc limit #{size}

mapper.update:
update t_ent_out_put_info
ent_code = #{entCode}, out_put_code = #{outPutCode}, out_put_name = #{outPutName}, out_put_type = #{outPutType}, out_put_status = #{outPutStatus}, longitude = #{longitude}, latitude = #{latitude}, out_put_height = #{outPutHeight}, out_put_position = #{outPutPosition}, mn_num = #{mnNum}, trans_rate = #{transRate}, valid_rate = #{validRate}, update_user = #{updateUser}, update_time = #{updateTime}, remark = #{remark}, pollutant_code = #{pollutantCode},
where out_put_id = #{outPutId}

mapper.select:
select out_put_id as outPutId, count(id) as `count` from t_bas_user_put_info
where user_id = #{userId}
group by out_put_id
