mapper.update:
update t_ent_output_pollutant
pollutant_code = #{pollutantCode}, pollutant_sort = #{pollutantSort}, alarm_type = #{alarmType}, is_zero_alarm = #{isZeroAlarm}, is_continuity_alarm = #{isContinuityAlarm}, standard_val = #{standardVal}, annual_limit_value = #{annualLimitValue}, monthly_limit_value = #{monthlyLimitValue}, early_warn_maxValue = #{earlyWarnMaxvalue}, early_warn_minValue = #{earlyWarnMinvalue}, over_maxValue = #{overMaxvalue}, over_minValue = #{overMinvalue}, exception_maxValue = #{exceptionMaxvalue}, exception_minValue = #{exceptionMinvalue}, update_user = #{updateUser}, update_time = #{updateTime}, mon_factor = #{monFactorStr},
where out_put_poll_id = #{outPutPollId}

update t_ent_out_put_info
set pollutant_code = (
    select GROUP_CONCAT(pollutant_code ORDER BY pollutant_sort DESC SEPARATOR ',')
    from t_ent_output_pollutant
    where out_put_id = #{outPutId}
)
where out_put_id = #{outPutId}
