mapper.select:
select
    u.id,
    u.pollutant_code,
    c.pollutant_name_cn,
    c.pollutant_name_en,
    u.mn_num,
    u.out_put_code,
    u.out_put_name,
    u.ent_code,
    u.ent_name,
    u.data_source,
    c.pollutant_unit_cn,
    c.pollutant_unit_en,
    u.pollutant_sort,
    u.alarm_type,
    u.is_zero_alarm,
    u.is_continuity_alarm,
    u.standard_val,
    u.early_warn_maxValue,
    u.early_warn_minValue,
    u.over_maxValue,
    u.over_minValue,
    u.exception_maxValue,
    u.exception_minValue,
    u.create_time,
    u.create_name,
    u.update_time,
    u.update_name,
    u.remark
from t_bas_gasoutput_pollutant_unorganized u
left join t_bas_pollutant_code c on c.pollutant_code = u.pollutant_code

    and u.pollutant_code = #{pollutantCode}
    and c.pollutant_name_cn = #{pollutantNameCn}

    and c.pollutant_name_en = #{pollutantNameEn}

    and u.mn_num = #{mnNum}
    and u.out_put_code = #{outPutCode}
    and u.out_put_name like concat('%', #{outPutName}, '%')

    and u.ent_code = #{entCode}
    and u.ent_name like concat('%', #{entName}, '%')
    and u.data_source = #{dataSource}
    and c.pollutant_unit_cn = #{pollutantUnitCn}

    and c.pollutant_unit_en = #{pollutantUnitEn}

    and u.pollutant_sort = #{pollutantSort}
    and u.alarm_type = #{alarmType}
    and u.is_zero_alarm = #{isZeroAlarm}
    and u.is_continuity_alarm = #{isContinuityAlarm}
    and u.standard_val = #{standardVal}
    and u.early_warn_maxValue = #{earlyWarnMaxvalue}
    and u.early_warn_minValue = #{earlyWarnMinvalue}
    and u.over_maxValue = #{overMaxvalue}
    and u.over_minValue = #{overMinvalue}
    and u.exception_maxValue = #{exceptionMaxvalue}
    and u.exception_minValue = #{exceptionMinvalue}
    and u.create_name like concat('%', #{createName}, '%')

    and u.update_name like concat('%', #{updateName}, '%')

mapper.update:
update t_bas_gasoutput_pollutant_unorganized
pollutant_code = #{pollutantCode}, pollutant_name_cn = #{pollutantNameCn}, pollutant_name_en = #{pollutantNameEn}, mn_num = #{mnNum}, out_put_code = #{outPutCode}, out_put_name = #{outPutName}, ent_code = #{entCode}, ent_name = #{entName}, data_source = #{dataSource}, pollutant_unit_cn = #{pollutantUnitCn}, pollutant_unit_en = #{pollutantUnitEn}, pollutant_sort = #{pollutantSort}, alarm_type = #{alarmType}, is_zero_alarm = #{isZeroAlarm}, is_continuity_alarm = #{isContinuityAlarm}, standard_val = #{standardVal}, early_warn_maxValue = #{earlyWarnMaxvalue}, early_warn_minValue = #{earlyWarnMinvalue}, over_maxValue = #{overMaxvalue}, over_minValue = #{overMinvalue}, exception_maxValue = #{exceptionMaxvalue}, exception_minValue = #{exceptionMinvalue}, create_time = #{createTime}, create_name = #{createName}, update_time = #{updateTime}, update_name = #{updateName}, remark = #{remark},
where id = #{id}
