mapper.select:
select
    info_id, user_name, ipaddr, login_location, browser, os, status, msg, login_time, system_key,
    system_id,
    (select menu_name from sys_menu where menu_key = sl.system_key and menu_key != 'system') as system_name
from
    sys_logininfor sl

	
		AND ipaddr like concat(concat('%', #{ipaddr}), '%')
	
	
		AND status = #{status}
	
	
		AND user_name like concat(concat('%', #{userName}), '%')
	
	
		AND login_time >= #{params.beginTime}
	
	
		AND login_time <= #{params.endTime}
	
	
		AND system_key = #{systemKey}
	
	
		AND system_id = #{systemId}
	
	
		AND system_name like concat('%', #{systemName}, '%')
	

order by login_time desc
